import React, { useState } from 'react';
import './PaymentForm.css'; // Import the CSS file for styling

const PaymentForm = () => {
    const [cardNumber, setCardNumber] = useState('');
    const [expirationDate, setExpirationDate] = useState('');
    const [cvv, setCvv] = useState('');
    const [message, setMessage] = useState('');

    const handlePayment = (e) => {
        e.preventDefault();
        // Add payment processing logic here
        setMessage('Payment successful!'); // Example success message
    };

    return (
        <div className="payment-container">
            <div className="payment-card">
                <h2 className="payment-title">Payment Information</h2>
                <form className="payment-form" onSubmit={handlePayment}>
                    <input
                        type="text"
                        value={cardNumber}
                        onChange={(e) => setCardNumber(e.target.value)}
                        placeholder="Card Number"
                        required
                        className="payment-input"
                    />
                    <input
                        type="text"
                        value={expirationDate}
                        onChange={(e) => setExpirationDate(e.target.value)}
                        placeholder="MM/YY"
                        required
                        className="payment-input"
                    />
                    <input
                        type="text"
                        value={cvv}
                        onChange={(e) => setCvv(e.target.value)}
                        placeholder="CVV"
                        required
                        className="payment-input"
                    />
                    <button type="submit" className="payment-button">Pay Now</button>
                    {message && <p className="payment-message">{message}</p>}
                </form>
            </div>
        </div>
    );
};

export default PaymentForm;
