// src/components/Payments.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Payments = () => {
    const [payments, setPayments] = useState([]);
    const [formData, setFormData] = useState({ amount: '', currency: '', swiftCode: '', recipientAccountNumber: '' });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    useEffect(() => {
        const fetchPayments = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5000/api/payments', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setPayments(response.data);
            } catch (err) {
                setError('Failed to fetch payments.');
            }
        };
        fetchPayments();
    }, []);

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handlePayment = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');
        try {
            const token = localStorage.getItem('token');
            await axios.post('http://localhost:5000/api/payments', formData, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setSuccess('Payment created successfully!');
        } catch (err) {
            setError('Payment failed.');
        }
    };

    return (
        <div>
            <h1>Payments</h1>
            <form onSubmit={handlePayment}>
                <input type="number" name="amount" onChange={handleInputChange} placeholder="Amount" required />
                <input type="text" name="currency" onChange={handleInputChange} placeholder="Currency (e.g., USD)" required />
                <input type="text" name="swiftCode" onChange={handleInputChange} placeholder="SWIFT Code" required />
                <input type="text" name="recipientAccountNumber" onChange={handleInputChange} placeholder="Recipient Account" required />
                <button type="submit">Submit Payment</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {success && <p style={{ color: 'green' }}>{success}</p>}
        </div>
    );
};

export default Payments;
