import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Payments.css'; // Import the CSS file

const Payments = () => {
    const [payments, setPayments] = useState([]);
    const [formData, setFormData] = useState({ amount: '', currency: '', swiftCode: '', recipientAccountNumber: '' });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const fetchPayments = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    console.warn("Token is missing in localStorage");
                    return;
                }
        
                const response = await axios.get('http://localhost:5000/api/payments', {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setPayments(response.data); // Update payments state with fetched data
            } catch (error) {
                console.error('Error fetching payments:', error);
            }
        };

        fetchPayments();
    }, []);

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handlePayment = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');
        setLoading(true); // Start loading state
        try {
            const token = localStorage.getItem('token');
            const response = await axios.post('http://localhost:5000/api/payments', formData, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setSuccess('Payment created successfully!');
            setPayments((prev) => [...prev, response.data]); // Update the payments state
        } catch (err) {
            console.error(err);
            const message = err.response?.data?.message || 'Payment failed.';
            setError(`Error ${err.response?.status}: ${message}`);
        } finally {
            setLoading(false); // End loading state
        }
    };

    return (
        <div className="payments-container">
            <h1 className="payments-title">Payments</h1>
            <form className="payments-form" onSubmit={handlePayment}>
                <input
                    type="number"
                    name="amount"
                    value={formData.amount}
                    onChange={handleInputChange}
                    placeholder="Amount"
                    required
                    className="payments-input"
                />
                <input
                    type="text"
                    name="currency"
                    value={formData.currency}
                    onChange={handleInputChange}
                    placeholder="Currency (e.g., USD)"
                    required
                    className="payments-input"
                />
                <input
                    type="text"
                    name="swiftCode"
                    value={formData.swiftCode}
                    onChange={handleInputChange}
                    placeholder="SWIFT Code"
                    required
                    className="payments-input"
                />
                <input
                    type="text"
                    name="recipientAccountNumber"
                    value={formData.recipientAccountNumber}
                    onChange={handleInputChange}
                    placeholder="Recipient Account"
                    required
                    className="payments-input"
                />
                <button type="submit" disabled={loading} className="payments-button">Submit Payment</button>
            </form>
            {loading && <p className="payments-loading">Loading...</p>}
            {error && <p className="payments-error">{error}</p>}
            {success && <p className="payments-success">{success}</p>}

            <h2 className="payments-history-title">Payment History</h2>
            {payments.length > 0 ? (
                <ul className="payments-list">
                    {payments.map((payment, index) => (
                        <li key={index} className="payments-list-item">
                            Amount: {payment.amount}, Currency: {payment.currency}, SWIFT Code: {payment.swiftCode}, Recipient Account: {payment.recipientAccountNumber}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No payments found.</p>
            )}
        </div>
    );
};

export default Payments;
