// client/src/components/PaymentForm.js
import React, { useState } from 'react';
import axios from 'axios';

const PaymentForm = ({ token }) => {
    const [amount, setAmount] = useState('');
    const [currency, setCurrency] = useState('');
    const [recipient, setRecipient] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post(`${process.env.REACT_APP_API_URL}/payments`, { amount, currency, recipient }, {
                headers: { Authorization: `Bearer ${token}` }, // Secure token in header
            });
            
          
            setMessage('Payment created successfully');
        } catch (err) {
            setMessage('Failed to create payment');
        }
    };

    return (
        <div>
            <h2>Create Payment</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="number"
                    placeholder="Amount"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Currency"
                    value={currency}
                    onChange={(e) => setCurrency(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Recipient"
                    value={recipient}
                    onChange={(e) => setRecipient(e.target.value)}
                    required
                />
                <button type="submit">Create Payment</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default PaymentForm;
