import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import PaymentForm from './components/PaymentForm';

const App = () => {
    // Initial token state based on localStorage
    const [token, setToken] = useState(localStorage.getItem('token') || null);

    // Save token to localStorage and trigger re-render on login
    const handleLogin = (newToken) => {
        setToken(newToken);
        localStorage.setItem('token', newToken); // Save token in localStorage
    };

    // If the token is missing, render the Login component
    return (
        <div>
            {token ? (
                <PaymentForm token={token} />
            ) : (
                <Login setToken={handleLogin} /> // Pass handleLogin as prop to set token correctly
            )}
        </div>
    );
};

export default App;
