import React, { useState } from 'react';
import Login from './components/Login';
import PaymentForm from './components/PaymentForm';

const App = () => {
    const [token, setToken] = useState(null);

    // Redirect function to go to payment form after login
    const navigateToPayments = () => {
        setToken(true); // Set to any truthy value to indicate login success
    };

    return (
        <div>
            {token ? (
                <PaymentForm token={token} />
            ) : (
                <Login setToken={setToken} navigateToPayments={navigateToPayments} />
            )}
        </div>
    );
};

export default App;
